package hm_day1;

public class Test11 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x = 100.8;
		double y = 20.6;
		// 定义变量，求和打印到控制台
		double add = x + y;
		System.out.println("x,y的和为:" + add);

		// 定义变量，求差打印到控制台
		double sub = x - y;
		System.out.println("x,y的差为:" + sub);

		// 定义变量，求积打印到控制台
		double mul = x * y;
		System.out.println("x,y的积为:" + mul);

		// 定义变量，求商打印到控制台 .
		double div = x / y;
		System.out.println("x,y的商为:" + div);
	}

}
