package hm_day2;

public class hm_07_符号和字符串 {

	/**
	 + : 在字符串中的操作，表示连接到字符串中
	 	 数字和数字之间还是加法运算
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("5"+ 5); //55
		System.out.println("5" + 5 + 5);//555
		System.out.println("5" + (5 + 5));//510
		System.out.println(5 + 5 + "5");//105
	}

}
