package hm_day3;

public class day3Test3 {

	/**
	 	编写步骤： 
	 		1.定义初始化变量i=1 
	 		2.使用while循环,条件为i<=10进入循环 
	 		3.在循环内输出i的值 
	 		4.在循环内编 写步进表达式为i++ 
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		while(i <= 10){
			System.out.println("i:" + i);
			i++;
		}
	}

}
